import axios from "axios";
import { useState } from "react";
import useFetch from "./useFetch";



const CreateBlog = () => {
    const [title, setTitle] = useState('');
    const [body, setBody] = useState('');
    const [id, setId] = useState(0);
    const [success, setSuccess] = useState(false);
    const { error, isPending, data: blogs } =  useFetch('http://localhost:8000/api/blog');
    let submitCount = 0;



    const handleSubmit = async (e) =>{
        e.preventDefault()
        console.log("Submitted");
        submitCount += 1;
        let blog = {title: title, body: body, id: 'user1_' + id};

        if (blogs !== null) {
            const blogIds = Object.keys(blogs).map(key => parseInt(blogs[key].id.split("_")[1]));
            const maxId = Math.max(...blogIds);
            blog.id = 'user1_' + (maxId + submitCount);
        }

        axios.post('http://localhost:8000/api/blog', blog)
        .then((res) =>{
            console.log(res);
            if(res.status === 200){
                setSuccess(true);
                setTimeout(() =>{
                    setSuccess(false);
                }, 2000)
            }
        })
    }

    return ( 
         <div className="create">
            <h2>Add a New Blog</h2>
            <form onSubmit={handleSubmit}>
                <label>Blog title:</label>
                <input
                    type="text"
                    required
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
                <label>Blog body:</label>
                <textarea
                    required
                    value={body}
                    onChange={(e) => setBody(e.target.value)}
                ></textarea>
                <label>Id Value</label>
                <input
                required
                type="number"
                value={id}
                onChange={(e) => setId(e.target.value)}
                />
                <button>Add Blog</button>
            </form>
            {success && <h2>Blog Created Successfully</h2>}
        </div>
        );
    }

export default CreateBlog;
