import axios from "axios";
import { useState } from "react";
import useFetch from "./useFetch";

async function getMaxBlogId() {
    const { error, isPending, data: blogs } = await useFetch('http://localhost:8000/api/blog');

    let blogIds = [];

    for (const key in blogs) {
      blogIds.push(parseInt(blogs[key].id.split("_")[1]));
    }

    let maxBlogId = Math.max(...blogIds);

    return maxBlogId;
  }

const CreateBlog = () => {
    const [title, setTitle] = useState('');
    const [body, setBody] = useState('');
    const [id, setId] = useState(null);

    useEffect(() => {
        async function fetchData() {
          let maxId = await getMaxBlogId();
          setId(maxId);
        }

        fetchData();
      }, []);


    const handleSubmit = (e) =>{
        e.preventDefault()
        console.log("Submitted");
        let blog = {title: title, body: body, id: 'user1_' + id};
        console.log(blog);


        axios.post('http://localhost:8000/api/blog', blog)
        .then((res) =>{
            console.log(res);
        })
    }

    return ( 
         <div className="create">
            <h2>Add a New Blog</h2>
            <form onSubmit={handleSubmit}>
                <label>Blog title:</label>
                <input
                    type="text"
                    required
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
                <label>Blog body:</label>
                <textarea
                    required
                    value={body}
                    onChange={(e) => setBody(e.target.value)}
                ></textarea>
                <label>Id Value</label>
                <input
                required
                type="number"
                value={id}
                onChange={(e) => setId(e.target.value)}
                />
                <button>Add Blog</button>
            </form>
        </div>
          );
        }

export default CreateBlog;
